body.script:
/* JS cho modal + accordion: ch·ªâ m·ªü 1 m·ª•c t·∫°i 1 th·ªùi ƒëi·ªÉm */
(function(){ function openModal(){ var m=document.getElementById('modalLuat'); if(m){ m.style.display='block'; m.setAttribute('aria-hidden','false'); } } function closeModal(){ var m=document.getElementById('modalLuat'); if(m){ m.style.display='none'; m.setAttribute('aria-hidden','true'); } } document.addEventListener('DOMContentLoaded', function(){ // m·ªü modal khi c√≥ n√∫t data-modal="modalLuat" document.querySelectorAll('[data-modal="modalLuat"]').forEach(function(btn){ btn.addEventListener('click', function(e){ e.preventDefault(); openModal(); }); }); // ƒë√≥ng n√∫t X var closes = document.querySelectorAll('#modalLuat .close'); closes.forEach(function(c){ c.addEventListener('click', closeModal); }); // click ra ngo√†i ƒë√≥ng window.addEventListener('click', function(e){ var m = document.getElementById('modalLuat'); if(m && e.target === m) closeModal(); }); // accordion: ch·ªâ 1 m·ªü var titles = document.querySelectorAll('#modalLuat .acc-title'); titles.forEach(function(t){ t.addEventListener('click', function(){ var allContents = document.querySelectorAll('#modalLuat .acc-content'); allContents.forEach(function(c){ c.classList.remove('open'); c.style.display='none'; }); var next = t.nextElementSibling; if(next){ next.classList.add('open'); next.style.display='block'; // ensure modal scroll to show opened content top var modalContent = document.querySelector('#modalLuat .modal-content'); if(modalContent){ var top = next.offsetTop - 20; modalContent.scrollTop = top; } } }); }); });
})();

