/* ==== Modal Control ==== */
document.addEventListener("DOMContentLoaded", () => { document.querySelectorAll("[data-modal]").forEach(btn => { btn.addEventListener("click", () => { const id = btn.getAttribute("data-modal"); document.getElementById(id).style.display = "block"; }); }); document.querySelectorAll(".modal .close").forEach(btn => { btn.addEventListener("click", e => { e.target.closest(".modal").style.display = "none"; }); }); window.addEventListener("click", e => { document.querySelectorAll(".modal").forEach(m => { if (e.target === m) m.style.display = "none"; }); });
}); /* ==== Config ==== */
const DATA_URL = "https://raw.githubusercontent.com/jobgiahieu/thi_sat_hach_gplx/main/gplx600_clean.json"; const IMAGE_BASE = "https://raw.githubusercontent.com/jobgiahieu/thi_sat_hach_gplx/main/images/"; const examRules = { "B": { questions: 30, time: 20 }, "C1": { questions: 40, time: 22 }, "C": { questions: 40, time: 22 }, "D1": { questions: 40, time: 22 }, "D2": { questions: 40, time: 22 }, "BE": { questions: 40, time: 22 }, "C1E": { questions: 40, time: 22 }, "CE": { questions: 40, time: 22 }, "D1E": { questions: 40, time: 22 }, "D2E": { questions: 40, time: 22 }, "DE": { questions: 40, time: 22 }, "A1": { questions: 25, time: 19 }, "A2": { questions: 25, time: 19 }, "A3": { questions: 25, time: 19 }, "A4": { questions: 25, time: 19 }
}; let allQuestions = [], selectedQuestions = [], answers = {};
let idx = 0, finished = false, timerInterval, timeLeft = 0, passingScore = 0;
let lastLicense = null, lastMode = "random", lastFixedNo = 1; function resolveImage(u){ if(!u) return ""; if(/^https?:/.test(u)) return u; return IMAGE_BASE+u; }
function ceil85(n){ return Math.ceil(n*0.85); }
function shuffle(a){ let arr=a.slice(); for(let i=arr.length-1;i>0;i--){ const j=Math.floor(Math.random()*(i+1)); [arr[i],arr[j]]=[arr[j],arr[i]];} return arr; } async function loadAllQuestions(){ const res=await fetch(DATA_URL); allQuestions=await res.json(); updateRuleInfo(); } const licenseEl=document.getElementById("license"), fixedNoEl=document.getElementById("fixedNo"), ruleInfoEl=document.getElementById("ruleInfo");
document.querySelectorAll('input[name="mode"]').forEach(r=>{r.addEventListener('change',()=>{fixedNoEl.disabled=(getMode()!=="fixed");});});
licenseEl.addEventListener("change", updateRuleInfo);
function getMode(){ return document.querySelector('input[name="mode"]:checked').value; }
function updateRuleInfo(){ const lic=licenseEl.value, rule=examRules[lic]; const pass=ceil85(rule.questions); ruleInfoEl.innerHTML=`H·∫°ng ${lic}: ${rule.questions} c√¢u - ${rule.time} ph√∫t. C·∫ßn ≥ ${pass} c√¢u ƒë√∫ng ƒë·ªÉ ƒê·∫†T.`; }
document.getElementById("btnStart").addEventListener("click",()=>{ startExam(licenseEl.value,getMode(),Number(fixedNoEl.value||1)); }); function startExam(license,mode,fixedNo=1){ lastLicense=license; lastMode=mode; lastFixedNo=fixedNo; const rule=examRules[license]; passingScore=ceil85(rule.questions); selectedQuestions=shuffle(allQuestions).slice(0,rule.questions).map(q=>({...q,image:resolveImage(q.image||"")})); idx=0; answers={}; finished=false; clearInterval(timerInterval); timeLeft=rule.time*60; startTimer(); document.getElementById("homeScreen").classList.add("hidden"); document.getElementById("examScreen").classList.remove("hidden"); document.getElementById("report").classList.add("hidden"); document.getElementById("result").innerHTML=""; updateMeta(license,mode,fixedNo); renderQuestion(); paintGrid();
} function updateMeta(license,mode,fixedNo){ const rule=examRules[license]; document.getElementById("examMeta").innerHTML=`H·∫°ng ${license} | ${(mode==="random"?"Ng·∫´u nhi√™n":`C·ªë ƒë·ªãnh #${fixedNo}`)} | ${rule.questions} c√¢u | C·∫ßn ≥ ${passingScore} c√¢u ƒë√∫ng`; }
function startTimer(){ const t=document.getElementById("timer"); timerInterval=setInterval(()=>{ if(timeLeft<=0){submitQuiz(true);return;} timeLeft--; const m=Math.floor(timeLeft/60),s=timeLeft%60; t.textContent=`${m}:${s<10?"0":""}${s}`; },1000);}
