function renderReportContent(mode){ let repHTML=""; selectedQuestions.forEach((q,i)=>{ if(mode==="fatal" && !q.isFatal) return; const yourIdx=answers[i]; const yourAns=(yourIdx!==undefined)?q.answers[yourIdx]:"‚ùå Kh√¥ng tr·∫£ l·ªùi"; const correctAns=q.answers[q.correct]; const isCorrect=(yourIdx===q.correct); const status=isCorrect?'‚úÖ ƒê√∫ng':'‚ùå Sai'; let fatalClass="", fatalNote=""; if(q.isFatal){ if(isCorrect){ fatalClass="fatal-correct"; fatalNote=" (C√¢u li·ªát - Tr·∫£ l·ªùi ƒë√∫ng)"; } else { fatalClass="fatal-wrong"; fatalNote=" (C√¢u li·ªát - Sai l√† r·ªõt ngay)"; } } repHTML+=`

